import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from './mockApi'; // Use the mocked axios instance

// Placeholder function to simulate fetching data (You can replace this with actual API call)
const fetchData = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        items: 10,
        value: 1000,
      });
    }, 1000);
  });
};

const Dashboard = () => {
  // State to hold data
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [muddamaalData, setMuddamaalData] = useState([])
  // Fetch data from mock API
  useEffect(() => {
    axios.get('/api/muddamaal')
        .then(response => {
            setMuddamaalData(response.data);
            setLoading(false);
        })
        .catch(error => console.log(error));
    }, []);

  // useEffect to fetch data when component mounts
  useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData();
        setData(result);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data', error);
        setLoading(false);
      }
    };

    getData();
  }, []);

  return (
    <Container fluid>
      <Row>
        <Col>
          <h1 className="my-4">Dashboard</h1>
        </Col>
      </Row>

      <Row>
        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>Item Statistics</Card.Title>
              {loading ? (
                <p>Loading...</p>
              ) : (
                <>
                  <p>Total Items: {data?.items}</p>
                  <p>Total Value: â‚¹{data?.value}</p>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>

        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>Manage Muddamaal</Card.Title>
              <Button variant="primary">Add New Item</Button>
              <Button variant="secondary" className="ml-3">
                View All Items
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
