import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Modal, Tab, Nav } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import Chart from 'chart.js';
import policeLogo from '../mumbai_logo.png';
import '../cssModules/Dashboard.css';
import axios from '../mockApi'; // Import the mock API

const Dashboard = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [mudemaalData, setMudemaalData] = useState([]); // State to store mudemaal data
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const navigate = useNavigate();

  // Fetch mudemaal data from mock API
  useEffect(() => {
    const fetchMudemaalData = async () => {
      try {
        const response = await axios.get('/api/muddamaal');
        setMudemaalData(response.data);
      } catch (error) {
        console.error('Error fetching mudemaal data:', error);
      }
    };
    fetchMudemaalData();
  }, []);

  // Calculate stats for the 4 boxes
  const totalIssues = mudemaalData.length;
  const totalCost = mudemaalData.reduce((sum, item) => sum + item.value, 0);
  const totalVehicles = mudemaalData.filter(item => item.category === 'Vehicle').length;
  const poStaysAt = 'Mumbai' // Assuming static data for now, you can change this as per your data

  // Update time every second
  useEffect(() => {
    const interval = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  // Initialize chart with fetched data
  useEffect(() => {
    if (mudemaalData.length > 0) {
      const categoryCounts = mudemaalData.reduce((acc, item) => {
        acc[item.category] = (acc[item.category] || 0) + 1;
        return acc;
      }, {});

      const ctx = document.getElementById('myChart').getContext('2d');
      new Chart(ctx, {
        type: 'pie',
        data: {
          labels: Object.keys(categoryCounts),
          datasets: [
            {
              data: Object.values(categoryCounts),
              backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
            },
          ],
        },
      });
    }
  }, [mudemaalData]);

  const handleLogout = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    setShowLogoutModal(false);
    navigate('/');
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  return (
    <Container fluid>
      {/* Header */}
      <Row className="header-row align-items-center">
        <Col md={2} className="logo-container">
          <img src={policeLogo} alt="Maharastra Police Logo" className="police-logo" />
        </Col>
        <Col md={8} className="text-center title-container">
          <h1 className="website-title">Mumbai Police - Muddamaal Management</h1>
          <p className="current-time">{currentTime.toLocaleString()}</p>
        </Col>
        <Col md={1}>
          <button className="btn btn-danger" onClick={handleLogout}>Log Out</button>
        </Col>
      </Row>

      {/* Four Boxes */}
      <Row className="info-box-row text-center my-4">
        <Col md={3}>
          <div className="info-box">
            <h3>Total Issues</h3>
            <p>{totalIssues}</p>
          </div>
        </Col>
        <Col md={3}>
          <div className="info-box">
            <h3>Total Cost</h3>
            <p>{totalCost}</p>
          </div>
        </Col>
        <Col md={3}>
          <div className="info-box">
            <h3>Total Vehicles</h3>
            <p>{totalVehicles}</p>
          </div>
        </Col>
        <Col md={3}>
          <div className="info-box">
            <h3>P.O. Stays At</h3>
            <p>{poStaysAt}</p>
          </div>
        </Col>
      </Row>

      {/* Two Flex Sections */}
      <Row className="flex-section">
        {/* First Flex (2 parts) */}
        <Col md={6}>
          {/* First part (Type of Issues Table) */}
          <Row className="mb-3">
            <Col>
              <h4>Types of Issues</h4>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Category</th>
                    <th>Quantity</th>
                  </tr>
                </thead>
                <tbody>
                  {mudemaalData.map((item) => (
                    <tr key={item.id}>
                      <td>{item.category}</td>
                      <td>{item.quantity}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Col>
          </Row>

          {/* Second part (2 Tables: Current Location and Reason of Impounding) */}
          <Row>
            <Col md={6}>
              <h4>Current Location of Item</h4>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Location</th>
                  </tr>
                </thead>
                <tbody>
                  {mudemaalData.map((item) => (
                    <tr key={item.id}>
                      <td>{item.muddamaalNo}</td>
                      <td>{item.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Col>
            <Col md={6}>
              <h4>Reason for Impounding</h4>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Reason</th>
                  </tr>
                </thead>
                <tbody>
                  {mudemaalData.map((item) => (
                    <tr key={item.id}>
                      <td>{item.muddamaalNo}</td>
                      <td>{item.category === 'Vehicle' ? 'Impounded for legal reasons' : 'Other'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Col>
          </Row>
        </Col>

        {/* Second Flex (Pie Chart) */}
        <Col md={6}>
          <h4>Category Distribution</h4>
          <canvas id="myChart"></canvas>
        </Col>
      </Row>

      {/* Logout Confirmation Modal */}
      <Modal show={showLogoutModal} onHide={cancelLogout}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Logout</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to log out?</Modal.Body>
        <Modal.Footer>
          <button className="btn btn-secondary" onClick={cancelLogout}>
            Cancel
          </button>
          <button className="btn btn-danger" onClick={confirmLogout}>
            Confirm
          </button>
        </Modal.Footer>
      </Modal>

      {/* Footer */}
      <Row>
        <Col className="text-center">
          <p>Developed by Your Company</p>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
